import React from "react";
import "./style.css"


const MakeCall = () => {
    let configuration = {
        'uri': '704@admin', // FILL SIP URI HERE like sip:sip-user@your-domain.bwapp.bwsip.io
        'password': '704704', // FILL PASSWORD HERE,
        'ws_servers': 'wss://77.104.116.187:7000'
    };

    let incomingCallAudio = new window.Audio('https://code.bandwidth.com/media/incoming_alert.mp3');
    incomingCallAudio.loop = true;
    let remoteAudio = new window.Audio();
    remoteAudio.autoplay = true;

    let callOptions = {
        mediaConstraints: {audio: true, video: false}
    };

    let phone;
    if(configuration.uri && configuration.password){
        JsSIP.debug.enable('JsSIP:*'); // more detailed debug output
        phone = new JsSIP.UA(configuration);
        phone.on('registrationFailed', function(ev){
            alert('Registering on SIP server failed with error: ' + ev.cause);
            configuration.uri = null;
            configuration.password = null;
            updateUI();
        });
        phone.on('newRTCSession',function(ev){
            var newSession = ev.session;
            if(session){ // hangup any existing call
                session.terminate();
            }
            session = newSession;
            var completeSession = function(){
                session = null;
                updateUI();
            };
            session.on('ended', completeSession);
            session.on('failed', completeSession);
            session.on('accepted',updateUI);
            session.on('confirmed',function(){
                var localStream = session.connection.getLocalStreams()[0];
                var dtmfSender = session.connection.createDTMFSender(localStream.getAudioTracks()[0])
                session.sendDTMF = function(tone){
                    dtmfSender.insertDTMF(tone);
                };
                updateUI();
            });
            session.on('addstream', function(e){
                incomingCallAudio.pause();
                remoteAudio.src = window.URL.createObjectURL(e.stream);
            });
            if(session.direction === 'incoming'){
                incomingCallAudio.play();
            }
            updateUI();
        });
        phone.start();
    }
    return (

        <React.Fragment>
            <div id="divCalling" className="demo-block">
                <button type="text" onClick={makeCall}>register</button>
                <div id="dialpad">
                    <table>
                        <tbody>
                        <tr>
                            <td><input type="button" value="1"/></td>
                            <td><input type="button" value="2"/></td>
                            <td><input type="button" value="3"/></td>
                        </tr>
                        <tr>
                            <td><input type="button" value="4"/></td>
                            <td><input type="button" value="5"/></td>
                            <td><input type="button" value="6"/></td>
                        </tr>
                        <tr>
                            <td><input type="button" value="7"/></td>
                            <td><input type="button" value="8"/></td>
                            <td><input type="button" value="9"/></td>
                        </tr>
                        <tr>
                            <td><input type="button" value="*"/></td>
                            <td><input type="button" value="0"/></td>
                            <td><input type="button" value="#"/></td>
                        </tr>
                        </tbody>
                    </table>
                </div>


            </div>
        </React.Fragment>
    )
};

export default MakeCall;
