import React, {useEffect, useState} from "react";
import avatarImage from "../../images/avatar.jpg";
import {useDispatch, useSelector} from "react-redux";
import RosterStatusDetector from "../roster-status-detector";
import $ from "jquery";
import "./style.css";

const ContactItem = (props) => {
    const dispatch = useDispatch();
    const connection = useSelector(state => state.connection);
    const $iq = useSelector(state => state.$iq);
    const showMainPage = useSelector(state => state.showMainPage);
    const [lastActivity, setLastActivity] = useState({from: '', time: ''})
    const handleRosterClick = (contact) => {
        console.log(contact);
        dispatch({contact, type: "SET_SELECTED_ROSTER"});
    };
    const getLastActivity = () => {
        let iq = $iq({
            type: "get",
            from: connection.jid,
            to: props.item.jid,
            id: "last1"
        }).c("query", {xmlns: "jabber:iq:last"});
        connection.sendIQ(iq, onLastActivity);
    };
    // useEffect(() => {
    //     getLastActivity();
    // }, []);
    const onLastActivity = (iq) => {
        debugger
        let from = $(iq).attr("from"); // the jabber_id of the contact\+
        let lastActivityObj;
        let errorType = $(iq).attr('type');
        console.log("errorType", errorType);

        $(iq).find("query").each(function () {

            if (errorType === "result") {
                let lastActivityTime = $(iq).find("query").attr('seconds');
                lastActivityObj = {
                    from: from,
                    time: lastActivityTime
                };
                console.log("lastActivityObj", lastActivityObj);
                setLastActivity({...lastActivity, from: lastActivityObj.from, time: lastActivityObj.time});
            }
            if (errorType === "error") {
                let errorMessage = $(iq).find("text").text();
                console.log("errorMessage", errorMessage)
            }

        });


    };
    const millisToMinutesAndSeconds = (millis) => {
        let minutes = Math.floor(millis / 60000);
        let seconds = ((millis % 60000) / 1000).toFixed(0);
        return minutes;
    };
    return (
        <React.Fragment>
            {/*<button onClick={getLastActivity}>last activity</button>*/}
            {/*<p>{lastActivity === 0 ? "online" : millisToMinutesAndSeconds(lastActivity.time) <= 30*/}
            {/*    ? "last seen recently" : millisToMinutesAndSeconds(lastActivity.time) < 1440*/}
            {/*        ? "last seen within a day" : "last seen long time ago"}</p>*/}
            <p>{lastActivity.time}</p>
            <div
                onClick={() => handleRosterClick(props.item.jid)}
                className="row item"
                id={props.item.jid}
            >
                <div className="avatar">

                    <RosterStatusDetector/>
                    <img alt="avatar" src={avatarImage}/>
                </div>
                <div className="right-side">
                    <div className="extension">
                        <p>112</p>
                    </div>

                    <div className="contact-name">
                        <p>{props.item.name}</p>
                    </div>
                </div>
            </div>

        </React.Fragment>
    );

};


export default ContactItem;
