import React from "react";
import {makeStyles, Theme, createStyles} from '@material-ui/core/styles';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';

class RadioButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            statuses: this.props.statuses,
            status: ''
        };
        this.handleChange = this.handleChange.bind(this);

    }

    handleChange(event) {
        this.setState({
            status: event.target.value
        })
    }

    render() {
        return (
            <React.Fragment>

                <FormControl component="fieldset">
                    <RadioGroup aria-label="My Status" name="My Status" value={this.state.status} onChange={this.handleChange}>
                        <FormControlLabel value="online" control={<Radio/>} label="Online"/>
                        <FormControlLabel value="away" control={<Radio/>} label="Away"/>
                        <FormControlLabel value="dnd" control={<Radio/>} label="Do not Disturb"/>
                        <FormControlLabel value="offline" control={<Radio/>} label="Offline"/>
                        <FormControlLabel
                            value="disabled"
                            disabled
                            control={<Radio/>}
                            label="(Disabled option)"
                        />
                    </RadioGroup>
                </FormControl>

            </React.Fragment>
        );
    }
}

export default RadioButton;
