import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import Device from "../Device";
import Extension from "../extension";
import CallLogs from "../call-log";
import Modal from "../../modal";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSpinner} from "@fortawesome/free-solid-svg-icons";
import "./style.css"
import MakeCall from "../make-call";
import RegisterDevice from "../register-device";
import axios from "axios";

const CallInformation = () => {
    const loading = useSelector(state => state.loadingStatus);
    const [loadingStatus, setLoadingStatus] = useState(true);
    const callFlowsData = useSelector(state => state.callFlowsData);

    const dispatch = useDispatch();
    useEffect(() => {
        setLoadingStatus(false);
        dispatch({loadingStatus, type: "SET_LOADING_STATUS"});
    }, [callFlowsData]);
    const getCookie = (cname) => {
        let name = cname + "=";
        let decodedCookie = decodeURIComponent(document.cookie);
        let ca = decodedCookie.split(';');
        for (let i = 0; i < ca.length; i++) {
            let c = ca[i];
            while (c.charAt(0) == ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) == 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    };
    const handleSubmit = () => {
        axios.get("http://77.104.116.187:8000/v2/accounts/" + getCookie("accID") + "/users/" + getCookie("owID") + "/devices", {
            headers: {
                "X-Auth-Token": getCookie("monster_token")
            }
        })
            .then(response => {
                let devices = devicesData.item;
                devices.push(response.data)
                getDevicesData({...devicesData, item: devices});
                setDeviceDataToReducer(devicesData.item[0].data);
            })
            .catch(error => {
                console.log(error);
            });
    };
    return (

        <React.Fragment>
            <div className="container">
                <button
                    type="button"
                    className="btn btn-primary"
                    id="connect"
                    value="Logout"
                    onClick={handleSubmit}
                >Log out From Kazoo</button>

                <div><Modal/></div>
                <div>{
                    loading ?
                        <div className="call-spinner"><FontAwesomeIcon className="fas fa-spinner fa-spin" icon={faSpinner}/>
                        </div> :
                        <div className="info">
                            <Device/>
                            <Extension/>
                            <CallLogs/>
                        </div>
                }</div>
            </div>
        </React.Fragment>
    )
};


export default CallInformation;
