import React, {useEffect, useState} from "react";
import {useSelector} from "react-redux";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faArrowLeft, faArrowRight, faBan} from "@fortawesome/free-solid-svg-icons";
import {myTime} from "../../helper/functions";
import {convert} from "../../helper/functions";

const GroupedLog = () => {
    const cdrsData = useSelector(state => state.cdrsData);
    const jalali = require('moment-jalaali');
    const [allDates, getAllDates] = useState({item: []});
    const moment = require('persian-date');
    const orgMoment = require('moment');

    const checkSameDayObjects = (array) => {

        let group = array.reduce((r, a) => {
            r[moment.unix(a.unix_timestamp).utc().format('YYYY/M/D')] = [...r[moment.unix(a.unix_timestamp).utc().format('YYYY/M/D')] || [], a];
            return r;
        }, {});
        console.log("group", group);
        getAllDates(group);
        let allDateArray = allDates.item;
        allDateArray.push(group);
        getAllDates({...allDates, item: allDateArray});

    };
    useEffect(() => {
        checkSameDayObjects(cdrsData);
        console.log("allDates", allDates);

    }, [cdrsData]);

    return (
        <React.Fragment>
            {allDates.length > 0 ?

                allDates.item[1].forEach((obj, i) => {
                    console.log("item", obj)
                    obj[i].map((item, i) => {
                        return (
                            <div className="call-info" key={i}>
                                {item.key}
                                <div className="left-side"> <span className="direction">
                                {
                                    item.direction === "inbound" ?
                                        <FontAwesomeIcon color="green" icon={faArrowLeft}/> :
                                        item.direction === "outbound" && i.hangup_cause !== "NO_ANSWER" ?
                                            <FontAwesomeIcon color="orange" icon={faArrowRight}/> :
                                            item.direction === "outbound" && i.hangup_cause === "NO_ANSWER" ?
                                                <FontAwesomeIcon color="red" icon={faBan}/> : null
                                }
                            </span>
                                    <div className="name">
                                        {
                                            item.direction === "inbound" ?
                                                item.callee_id_name + "(" + item.callee_id_number + ")" :
                                                item.direction === "outbound" && i.hangup_cause !== "NO_ANSWER" ?
                                                    item.caller_id_name + "(" + item.caller_id_number + ")" :
                                                    item.direction === "outbound" && i.hangup_cause === "NO_ANSWER" ?
                                                        item.caller_id_name + "(" + item.caller_id_number + ")" : null
                                        }

                                        <div className="duration">

                                            {
                                                item.direction === "inbound" ?
                                                    <span>Incoming call, </span>
                                                    :
                                                    item.direction === "outbound" && i.hangup_cause !== "NO_ANSWER" ?
                                                        <span>Outgoing call, </span>
                                                        :
                                                        item.direction === "outbound" && i.hangup_cause === "NO_ANSWER" ?
                                                            <span>Missed call, </span>
                                                            : null

                                            }
                                            {myTime(item.duration_seconds)}
                                        </div>
                                    </div>
                                </div>
                                <div className="date">

                                    {
                                        jalali.unix(item.unix_timestamp).format('jYYYY/jM/jD, HH:mm:ss')
                                    }

                                </div>


                            </div>
                        )
                    })


                })
                : null
            }

        </React.Fragment>
    );

};


export default GroupedLog;
