import React from "react";
import {useSelector} from "react-redux";

const CallLogs = () => {
    const cdrsData = useSelector(state => state.cdrsData);
    const moment = require('persian-date');
    const orgMoment = require('moment');

    const convertTimeStampToTime = (timestamp) => {
        console.log('timestamp', timestamp);
        // let time = moment(timestamp, 'YYYY-M-D HH:mm:ss');
        let convdataTime = new moment(timestamp).format();
        return convdataTime;
    };
    const checkTodayCallLog = (timestamp) => {
        let today = new Date();
        let myDate = orgMoment(timestamp);
        console.log(today);
        console.log("myDate", myDate)
        // if(timestamp === today){
        //     console.log()
        // }
        isToday(orgMoment(timestamp).format())
    };
    const isToday = (someDate) => {
        const today = new Date()
        return someDate.getDate() == today.getDate() &&
            someDate.getMonth() == today.getMonth() &&
            someDate.getFullYear() == today.getFullYear()
    }

    return (
        <React.Fragment>
            <button className="btn-sm badge-light " onClick={checkTodayCallLog}>Today</button>
            <button className="btn-sm badge-light">This Week</button>
            <button className="btn-sm badge-light">This Month</button>
            <table className="table table-sm table-striped table-bordered table-hover">
                <thead>
                <tr>
                    <th>Date/Time</th>
                    <th>From</th>
                    <th>To</th>
                    <th>Duration</th>
                </tr>
                </thead>
                <tbody>{

                        cdrsData === undefined ? null : cdrsData.map((item, i) => {

                            return (
                                <tr key={i}>

                                    <td>

                                        {
                                            convertTimeStampToTime(item.unix_timestamp)
                                        }

                                    </td>
                                    <td>
                                        {item.from}
                                    </td>
                                    <td>
                                        {item.to}
                                    </td>
                                    <td>
                                        {item.duration_seconds}
                                    </td>
                                </tr>
                            )

                        })
                }
                </tbody>
            </table>
        </React.Fragment>
    )

};


export default CallLogs;
