import React, {useEffect, useState} from "react";
import axios from 'axios';
import {useSelector} from "react-redux";
import {showMainPage} from "../../../redux/action";

const KazooLogin = () => {
    const [jidValue, setJidValue] = useState('');
    const [passValue, setPassValue] = useState('');
    const [loginButton, changeLoginButton] = useState('connect');
    const showCallAppPage = useSelector(state => state.showMainPage);

    const onConnect = () => {
        axios.put('http://docker.nexfon.ir:8000/v2/user_auth', {
            data: {
                "account_name": jidValue.split("/", 1)[0],
                "credentials": "d2abaa37a7c3db1137d385e1d8c15fd2"
            }
        })
            .then(response => {
                console.log(response.data);
                setShowStatus(true);
            })
            .catch(error => {
                console.log(error);
                setShowStatus(false);

            });
    };

    /*---------------------------------*/
    //Show Or hide Main page.
    const setShowStatus = (showMainPage) => {
        dispatch({showMainPage, type: 'SHOW_MAIN_PAGE'})
    };
    /*---------------------------------*/
    //action for login form
    const handleChangeJID = (event) => {
        setJidValue(event.target.value)
    };
    const handleChangePass = (event) => {
        // console.log("passValue", event.target.value)
        setPassValue(event.target.value)
    };
    const handleSubmit = (event) => {
        event.preventDefault();
        if (jidValue === undefined || jidValue === "") {
            alert("Blank JID");
        } else if (passValue === undefined || passValue === "")
            alert("Empty pass, please type password");
        else {
            if (loginButton === "connect") {
                changeLoginButton("disconnect")
                onConnect();
            } else {
                changeLoginButton("connect")

            }
        }
    };
    /*-----------------------------------*/

    return (
        <React.Fragment>

            <div className="row">
                <div className="col">
                    <form>
                        <input
                            label="JID"
                            className="col-sm-2 col-form-label"
                            size="col-sm-10"
                            type="text"
                            placeholder="enter your JID..."
                            onChange={handleChangeJID}
                            value={jidValue}
                        />
                        <input
                            label="Password"
                            className="col-sm-2 col-form-label"
                            size="col-sm-10"
                            type="password"
                            placeholder="enter your password..."
                            onChange={handleChangePass}
                            value={passValue}
                        />

                        <button
                            type="button"
                            className="btn btn-primary"
                            id="connect"
                            value={loginButton}
                            onClick={handleSubmit}
                        >{loginButton}</button>
                    </form>
                </div>
            </div>
        </React.Fragment>
    )
}


export default KazooLogin;
